document.addEventListener("DOMContentLoaded",function(){let e=document.getElementById("burger-button"),t=document.getElementById("responsive-navbar");e.onclick=function(){e.classList.toggle("active-burger"),t.classList.toggle("active-responsive-navigation")};let n=document.querySelectorAll(".navigation-link");n.forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();e=this.getAttribute("href");document.querySelector(e).scrollIntoView({behavior:"smooth",block:"start"})})})});